package com.meepo.pro.splashimport com.meepo.basic.schema.AppSettingsimport com.meepo.sdk.base.mvp.BasePresenterimport com.meepo.sdk.observer.Observerimport com.meepo.sdk.observer.ObserverManager.applySchedulers/** * @author  佟旭 * @wechat tongxury * @date  2020/10/14 10:46 PM * @version 1.0 */class Presenter : BasePresenter<IContract.IView>(), IContract.IPresenter {    override fun fetchAppSettings() {        IService.get().fetchAppSettings()            .applySchedulers()            .map { rsp -> rsp.data }            .compose(mView?.bindToLife<AppSettings>())            .doFinally { mView?.doFinally() }            .subscribe(object : Observer<AppSettings>() {                override fun onSuccess(t: AppSettings?) {                }                override fun onFailure(e: Throwable) {                }            })    }}