package com.meepo.basic.authimport com.meepo.basic.schema.UserSummaryimport com.meepo.sdk.base.mvp.IBaseContract/** * @author  佟旭 * @wechat tongxury * @date  2020/10/14 10:47 PM * @version 1.0 */interface IContract {    interface IView : IBaseContract.IBaseView {        fun onCodeSent()        fun onLoginSuccess(userSummary: UserSummary)        fun onLoginFail(message: String)    }    interface IPresenter : IBaseContract.IBasePresenter {        fun sendAuthCode(phoneNo: String)        fun loginByCode(phoneNo: String, code: String)        fun loginByPassword(phoneNo: String, password: String)        fun loginByOneKey(accessToken: String)    }}