package com.meepo.feed.article.core.categorizedlistimport com.meepo.feed.Serviceimport com.meepo.feed.Storeimport com.meepo.feed.schema.Categoryimport com.meepo.sdk.framework.BasePresenterimport com.meepo.sdk.observer.Observerimport com.meepo.sdk.observer.ObserverManager.applySchedulers/** * @author  佟旭 * @wechat tongxury * @date  2020/10/14 10:46 PM * @version 1.0 */class Presenter : BasePresenter<IContract.IFeedView>(), IContract.IFeedPresenter {    override fun fetchCategories() {        Service.get().fetchCategories()            .applySchedulers()            .map { rsp -> rsp.data }            .compose(mView?.bindToLife<ArrayList<Category>>())            .subscribe(object : Observer<ArrayList<Category>>() {                override fun onSuccess(t: ArrayList<Category>?) {                    mView?.applyCategories(t)                }                override fun onFailure(e: Throwable) {                }            })    }}