package com.meepo.feed.article.core.detailimport com.meepo.feed.schema.Commentimport com.meepo.feed.schema.Itemimport com.meepo.sdk.framework.IPresenterimport com.meepo.sdk.framework.IView/** * @author  佟旭 * @wechat tongxury * @date  2020/10/14 10:47 PM * @version 1.0 */interface IContract {    interface IFeedView : IView {        fun appendComments(comments: List<Comment>?, total : Int, hasMore: Boolean)        fun applyComment(comment: Comment)        fun apply(item: Item)    }    interface IFeedPresenter : IPresenter {        fun starComment(id: String)        fun follow(item: Item)        fun fetchComments(targetId: String, targetType: String)        fun comment(item: Item, content: String)        fun share(item: Item)        fun collect(item: Item)        fun fetchDetail(id: String)    }}