package com.meepo.feed.article.core.search.listimport com.meepo.feed.Serviceimport com.meepo.feed.Storeimport com.meepo.feed.schema.FeedResultimport com.meepo.sdk.base.mvp.BasePresenterimport com.meepo.sdk.observer.Observerimport com.meepo.sdk.observer.ObserverManager.applySchedulers/** * @author  佟旭 * @wechat tongxury * @date  2020/10/14 10:46 PM * @version 1.0 */class Presenter : BasePresenter<IContract.IView>(), IContract.IPresenter {    override fun fetchMore(keyword: String) {        Service.get().fetchByKeyword(keyword)            .applySchedulers()            .map { rsp -> rsp.data }            .compose(mView?.bindToLife<FeedResult>())            .doFinally { mView?.doFinally() }            .subscribe(object : Observer<FeedResult>() {                override fun onSuccess(t: FeedResult?) {                    mView?.append(t)                }                override fun onFailure(e: Throwable) {                }            })    }}