package com.meepo.usercenter.profileimport com.meepo.basic.AppBasicModuleimport com.meepo.basic.schema.UserSummaryimport com.meepo.sdk.framework.BasePresenterimport com.meepo.sdk.observer.Observerimport com.meepo.sdk.observer.ObserverManagerimport com.meepo.sdk.observer.ObserverManager.applyObserverimport com.meepo.usercenter.Store/** * @author  佟旭 * @wechat tongxury * @date  2020/10/14 10:46 PM * @version 1.0 */class Presenter : BasePresenter<IContract.IProfileView>(), IContract.IProfilePresenter {    override fun getUserSummary() {        ObserverManager            .applyFunc { AppBasicModule.getUserSummary() }            .compose(mView?.bindToLife<UserSummary>())            .applyObserver(object : Observer<UserSummary>() {                override fun onSuccess(t: UserSummary?) {                    mView?.applyUserSummary(t)                }                override fun onFailure(e: Throwable) {                }            })    }}